import { uiActions } from "./ui-slice";
import { cartActions } from "./cart-slice";

export const fetchCartData = () => {
    return async (dispatch) => {
        const fetchData = async () => {
            const response = await fetch(
                "https://reactreduxstudy-b3951-default-rtdb.europe-west1.firebasedatabase.app/cart.json"
                );
            if (!response.ok) {
                throw new Error("Could not fetch cart data!");
            }
            const data = await response.json();
            return data;
        }
        try {
            const cartData = await fetchData();
            dispatch(cartActions.replaceCart({items: cartData.items || [], totalQuantity: cartData.totalQuantity}));
        } catch (error) {
            uiActions.showNotification({
                status: "error",
                title: "Error!",
                message: "Sending cart data failed!",
              })
        }
    }
}

export const sendCartData = (cart) => {
    return async (dispatch) => {
        dispatch(uiActions.showNotification({
            status: "pending",
            title: "Sending",
            message: "Sending cart data!"
        }));

        const sendRequest = async () => {
            const response = await fetch("https://reactreduxstudy-b3951-default-rtdb.europe-west1.firebasedatabase.app/cart.json", {
                method: "PUT",
                body: JSON.stringify({
                    items: cart.items, 
                    totalQuantity: cart.totalQuantity
                })
              });
              if (!response.ok) {
                throw new Error("Sending cart data failed.");
            }
        }
        try {
            sendRequest();
            dispatch(
                uiActions.showNotification({
                status: "succes",
                title: "Succes!",
                message: "Sent cart data!",
                })
            )
        } catch (error) {
            dispatch(
                uiActions.showNotification({
                  status: "error",
                  title: "Error!",
                  message: "Sending cart data failed!",
                })
              )
        }
    };
}
